import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import './SummaryScreen.css';


const SummaryScreen = ({ match }) => {
  const [summary, setSummary] = useState({});
  const history = useHistory();

  useEffect(() => {
    axios.get(`https://api.tvmaze.com/shows/${match.params.id}`)
      .then(response => {
        setSummary(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, [match.params.id]);

  const handleBooking = () => {
    localStorage.setItem('movieName', summary.name);
    localStorage.setItem('movieDetails', JSON.stringify(summary));
    history.push('/booking');
  };

  return (
    <div className="container">
      <h1>{summary.name}</h1>
      <p dangerouslySetInnerHTML={{ __html: show.show.summary }}>{summary.summary}</p>
      <button onClick={handleBooking}>Book
      </button>
    </div>
  );
};
export default SummaryScreen;
